using DataContracts = Microsoft.ApplicationInsights.DataContracts;

/// <summary>
/// Base class for Telemetry
/// </summary>
abstract class ISMTelemetryBase
{
    protected Map basePropertyMap = new Map(Types::String, Types::String);
    protected Map runtimePropertyMap = new Map(Types::String, Types::String);

    System.Diagnostics.Stopwatch stopWatch = new System.Diagnostics.Stopwatch();

    protected ISMTelemetryParameterSetup logParams;

    //you can fill this field or not, if you don't fill it, it will be set to 0
    protected str eventId;

    protected SysPackable telemetryConfig;

}

Method.Source:
protected void new()
{
    stopWatch = new System.Diagnostics.Stopwatch();
    stopWatch.Start();

    this.initialize();
}

/// <summary>
/// Initializes the telemetry.
/// </summary>
public void initialize()
{
    logParams = ISMTelemetryParameterSetup::find(this.telemetryName());

    if (!logParams)
    {
        this.initializeParameterSetup(logParams);
    }

    DictClass dictClass = new DictClass(className2Id(this.telemetryConfigurationName()));
    telemetryConfig = dictClass.makeObject();
    if (logParams.TelemetryConfiguration == conNull())
    {
        this.initializeConfiguration(telemetryConfig);
        logParams.TelemetryConfiguration = telemetryConfig.pack();
    }
    else
    {
        telemetryConfig.unpack(logParams.TelemetryConfiguration);
    }
}

/// <summary>
/// Initializes the parameter setup values.
/// </summary>
/// <param name = "_parameterSetup">The parameter setup record to initialize.</param>
protected void initializeParameterSetup(ISMTelemetryParameterSetup _parameterSetup)
{
    _parameterSetup.TelemetryClass = this.telemetryName();
    _parameterSetup.IsEnabled = NoYes::Yes;
    _parameterSetup.SeverityLevel = ISMTelemetrySeverityLevel::Information;
}

