/// Initializes the configuration.
/// </summary>
/// <param name = "_configuration">The configuration to initialize</param>
/// <remarks>
/// Override this method in the subclass if custom configuration initialization is required.
/// </remarks>
protected void initializeConfiguration(SysPackable _configuration)
{
    //no default implementation
}

/// <summary>
/// Gets the telemetry parameter setup
/// </summary>
/// <returns>The telemetry parameter setup</returns>
public ISMTelemetryParameterSetup getParameterSetup()
{
    return logParams;
}

