/// Overwrite this method in the subclass if a custom configuration is required for a telemetry.
/// If not overwritten, an empty dummy contract is used.
/// </remarks>
public MethodName getContractDefiningMethodName()
{
    return methodStr(ISMTelemetryBase, defineBaseContract);
}

/// <summary>
/// Method signature provides the contract class to the SysOperation framework to
/// dynamically generate the configuration dialog.
/// </summary>
/// <param name = "_contract">Parameter to define the contract class</param>
/// <remarks>
/// Method is not called, only defines the contract class via its signature.
/// If a telemetry requires custom configuration, create a new define[TelemetryName]Contract
/// method in the subclass. The method name needs to be specified by the override of the
/// getContractDefiningMethodName method.
/// </remarks>
public void defineBaseContract(ISMTelemetryBaseContract _contract)
{
}

/// <summary>
/// This method fills the configuration contract of the base telemetry with the necessary values.
/// </summary>
/// <param name = "_contract">The contract to fill with configuration values</param>
/// <remarks>
/// If a telemetry requires custom configuration, override this method in the telemetry 
/// subclass with the correct configuration class for the _contract parameter.
/// </remarks>
public void fillConfigurationContract(SysPackable _contract)
{
    ISMTelemetryBaseContract contract;
    if (_contract && _contract is ISMTelemetryBaseContract)
    {
        contract = _contract as ISMTelemetryBaseContract;
        if (telemetryConfig != null)
        {
            contract.unpack(telemetryConfig.pack());
        }
    }
}

/// <summary>
/// Sets the telemetry configuration based on the given contract.
/// </summary>
/// <param name = "_contract">The contract to use for the telemetry configuration.</param>
public void setConfigurationFromContract(SysPackable _contract)
{
    this.setTelemetryConfiguration(_contract);
}

/// <summary>
/// Sets the telemetry configuration of the base telemetry based on the given contract.
/// </summary>
/// <param name = "_config">The configuration contract to use for base telemetry setup.</param>
/// <remarks>
/// If a telemetry requires custom configuration, override this method in the telemetry with
/// the correct configuration class for the _config parameter.
/// </remarks>
protected void setTelemetryConfiguration(SysPackable _config)
{
    telemetryConfig = _config as ISMTelemetryBaseContract;
}

