SourceCode.Declaration:
/// <summary>
/// Empty dummy contract for the configuration of the base telemetry and other telemetries that
/// do not require a custom configuration.
/// </summary>
[DataContract]
class ISMTelemetryBaseContract implements SysPackable
{
    #define.CurrentVersion(1)

}

Method.Source:
public boolean unpack(container _packedData)
{
    int version = conPeek(_packedData, 1);
    switch (version)
    {
        case #CurrentVersion:
            [version] = _packedData;
            break;
        default:
            return false;
    }
    return true;
}
