/// <summary>
/// Handles ISM telemetry events from the SysIntParameters form.
/// </summary>
internal final class ISMTelemetryEventHandler
{


}

Method.Source:
/// <summary>
/// Initializes the telemetry parameter setups.
/// </summary>
/// <param name="sender">sender</param>
/// <param name="e">event arguments</param>
[SuppressBPWarning('BPParameterNotUsed', 'Parameter required by the event interface.'),
FormEventHandler(formStr(SysIntParameters), FormEventType::Initializing)]
public static void sysIntParameters_OnInitializing(xFormRun sender, FormEventArgs e)
{
    ISMTelemetryParameterSetup::initializeParameterSetups();
}

/// <summary>
/// Opens the telemetry configuration dialog for the selected telemetry parameter setup.
/// </summary>
/// <param name="sender">The sender of the event.</param>
/// <param name="e">Event arguments associated with the event.</param>
[
    SuppressBPWarning('BPParameterNotUsed', 'Parameter required by the event interface.'),
    FormControlEventHandler(
        formControlStr(SysIntParameters, ISMTelemetryConfiguration), 
        FormControlEventType::Clicked)
]
public static void ISMTelemetryConfiguration_OnClicked(
    FormControl sender, 
    FormControlEventArgs e)
{
    FormRun formRun = sender.formRun();
    FormDataSource formDataSource = 
        formRun.dataSource(formDataSourceStr(SysIntParameters, ISMTelemetryParameterSetup));
    ISMTelemetryParameterSetup telemetryParameterSetup = formDataSource.cursor();
    DictClass dictClass = new DictClass(className2Id(telemetryParameterSetup.TelemetryClass));
    ISMTelemetryBase telemetry = dictClass.makeObject();
    if (ISMTelemetryConfigurationDialogController::telemetryConfigurationDialog(telemetry))
    {
        telemetryParameterSetup.TelemetryConfiguration = telemetry.getConfigurationContainer();
    }
}

/// <summary>
/// Handles the click event for reinitializing telemetry parameter setups.
/// </summary>
/// <param name="sender">The sender of the event.</param>
/// <param name="e">Event arguments associated with the event.</param>
[
    SuppressBPWarning('BPParameterNotUsed', 'Parameter required by the event interface.'),
    FormControlEventHandler(
        formControlStr(SysIntParameters, ISMTelemetryReinitializeParameterSetups), 
        FormControlEventType::Clicked)
]
public static void ISMTelemetryReinitializeParameterSetups_OnClicked(
    FormControl sender, FormControlEventArgs e)
{
    ISMTelemetryParameterSetup::reinitializeParameterSetups();
    sender.formRun().dataSource(
        formDataSourceStr(SysIntParameters, ISMTelemetryParameterSetup)).research();
}
