SourceCode.Declaration:
/// <summary>
/// Contract for the infolog telemetry configuration.
/// </summary>
[
    DataContract,
    SysOperationGroup('AddCallStack', "@ISM:ISMTelemetryInfolog_AddCallStack", '1'),
    SysOperationGroup('CompressCallStack', "@ISM:ISMTelemetryInfolog_CompressCallStack", '2')
]
class ISMTelemetryInfologContract implements SysPackable
{
    private boolean addCallStackInformation;
    private boolean addCallStackWarning;
    private boolean addCallStackError;
    private boolean compressCallStack;

    #define.CurrentVersion(1)
    #localmacro.CurrentList
        addCallStackInformation,
        addCallStackWarning,
        addCallStackError,
        compressCallStack
    #endmacro

}

Method.Source:
[
    DataMember,
    SysOperationLabel("@ISM:ISMTelemetryInfolog_AddInformationCallStack"),
    SysOperationGroupMember('AddCallStack')
]
public boolean parmAddCallStackInformation(boolean _addCallStackInformation = addCallStackInformation)
{
    addCallStackInformation = _addCallStackInformation;
    return addCallStackInformation;
}

[
    DataMember,
    SysOperationLabel("@ISM:ISMTelemetryInfolog_AddWarningCallStack"),
    SysOperationGroupMember('AddCallStack')
]
public boolean parmAddCallStackWarning(boolean _addCallStackWarning = addCallStackWarning)
{
    addCallStackWarning = _addCallStackWarning;
    return addCallStackWarning;
}

[
    DataMember,
    SysOperationLabel("@ISM:ISMTelemetryInfolog_AddErrorCallStack"),
    SysOperationGroupMember('AddCallStack')
]
public boolean parmAddCallStackError(boolean _addCallStackError = addCallStackError)
{
    addCallStackError = _addCallStackError;
    return addCallStackError;
}

[
    DataMember,
    SysOperationLabel("@ISM:ISMTelemetryInfolog_CompressCallStack"),
    SysOperationGroupMember('CompressCallStack')
]
public boolean parmCompressCallStack(boolean _compressCallStack = compressCallStack)
{
    compressCallStack = _compressCallStack;
    return compressCallStack;
}

public boolean unpack(container _packedData)
{
    int version = conPeek(_packedData, 1);
    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = _packedData;
            break;
        default:
            return false;
    }
    return true;
}
