public class ISMTelemetryParameterSetup extends common
{
}

Method.Source:
/// <summary>
/// Finds the telemetry parameters for the given class.
/// </summary>
/// <param name = "_class"> classname </param>
/// <param name = "_forupdate">should update </param>
/// <returns>Returns the record</returns>
public static ISMTelemetryParameterSetup find(
    ISMTelemetryClass _class,
    boolean   _forupdate = false)
{
    ISMTelemetryParameterSetup    param;

    if (_class)
    {
        param.selectForUpdate(_forupdate);


        select firstonly param
            where param.TelemetryClass == _class;
    }

    return param;
}

/// <summary>
/// Initializes the parameter setups for all child classes of ISMTelemetryBase.
/// </summary>
public static void initializeParameterSetups()
{
    ClassName baseClassName = classStr(ISMTelemetryBase);
    SysDictClass dictClass = new SysDictClass(className2Id(baseClassName));
    List extendedClassList = dictClass.extendedBy();
    //loop through all child classes
    if (extendedClassList.elements())
    {
        ListEnumerator listEnumerator = extendedClassList.getEnumerator();
        ttsbegin;
        while (listEnumerator.moveNext())
        {
            ClassId childClassId = listEnumerator.current();
            ISMTelemetryBase telemetry = new SysDictClass(childClassId).makeObject();
            telemetry.initialize();
            ISMTelemetryParameterSetup telemetryParameterSetup = telemetry.getParameterSetup();
            if (!telemetryParameterSetup
                && telemetryParameterSetup.validateWrite())
            {
                telemetryParameterSetup.insert();
            }
        }
        ttscommit;
    }
}

/// <summary>
/// Reinitializes the parameter setups by deleting all existing setups and recreating them.
/// </summary>
public static void reinitializeParameterSetups()
{
    ISMTelemetryParameterSetup paramSetup;
    ttsbegin;
    delete_from paramSetup;
    ttscommit;

    ISMTelemetryParameterSetup::initializeParameterSetups();
}
